
public class Swapping1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 22;
		int b = 32;
		
		int t = a;
		
		System.out.println("Value of a " +a +b +t);
		
		a = b*t;
		b = a+t;
		
		System.out.println(a);
		System.out.println(t);

	}

}
